import {createSelector} from 'reselect';

const MAP_TO_ARRAY = {
    hats: "Hats",
    jackets : "Jackets",
    mens    : "Mens",
    sneakers : "Sneakers",
    womens  :   "Womens"
}

const selectShop = state => state.shop;

export const selectShopData = createSelector(
    [selectShop],
    (shop) => Object.keys(shop).map(key => shop[key])
)
