import CartActionTypes from "./cart.types";

const INITIAL_STATE = {
  hidden: true,
  cartItems : []
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case CartActionTypes.TOGGLE_HIDDEN_STATE:
      return {
        ...state,
        hidden: !state.hidden,
      };
    default:
      return {
        state,
      };
  }
};

export default cartReducer;
