import CartActionTypes from "./cart.types";

export const toggleCartHidden = () =>({
    type : CartActionTypes.TOGGLE_HIDDEN_STATE
}) 

export const addItem = (payload) =>({
    type : CartActionTypes.ADD_ITEMS(payload)
})
